/**
 * `confirm` type prompt
 */

import colors from 'yoctocolors-cjs';
import { take, takeUntil } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

export default class ConfirmPrompt extends Base {
  constructor(questions, rl, answers) {
    super(questions, rl, answers);

    let rawDefault = true;

    Object.assign(this.opt, {
      filter(input) {
        if (input != null && input !== '') {
          if (/^y(es)?/i.test(input)) return true;
          if (/^n(o)?/i.test(input)) return false;
        }
        return rawDefault;
      },
    });

    if (this.opt.default != null) {
      rawDefault = Boolean(this.opt.default);
    }

    this.opt.default = rawDefault ? 'Y/n' : 'y/N';
  }


  /**
   * Starts an Inquiry session.
   *
   * @param {Function} cb - Callback function to execute when the prompt is completed.
   * @return {this}
   *
   * @example
   * inquirySession.run((result) => {
   *   console.log('Inquiry completed with result:', result);
   * });
   */
  _run(cb) {
    this.done = cb;

    // Once user confirm (enter key)
    const events = observe(this.rl);
    events.keypress.pipe(takeUntil(events.line)).forEach(this.onKeypress.bind(this));

    events.line.pipe(take(1)).forEach(this.onEnd.bind(this));

    // Init
    this.render();

    return this;
  }


  /**
   * Render the prompt to screen.
   *
   * @param {boolean|string} answer - The user's input, which can be a boolean or a string. If `true`, appends 'Yes' in cyan; if `false`, appends 'No' in cyan; otherwise, appends the provided string or the current readline line.
   * @return {ConfirmPrompt} self - Returns the instance of ConfirmPrompt for method chaining.
   */
  render(answer) {
    let message = this.getQuestion();

    if (typeof answer === 'boolean') {
      message += colors.cyan(answer ? 'Yes' : 'No');
    } else if (answer) {
      message += answer;
    } else {
      message += this.rl.line;
    }

    this.screen.render(message);

    return this;
  }


  /**
   * Handles the end of user input when the `enter` key is pressed.
   *
   * @param {string} input - The user input provided.
   * @returns {void}
   */
  onEnd(input) {
    this.status = 'answered';

    let output = this.opt.filter(input);
    if (this.opt.transformer) {
      output = this.opt.transformer(output);
    }
    this.render(output);

    this.screen.done();
    this.done(output);
  }


  /**
   * This method is triggered when a user presses a key. It invokes the `render` method to update the display or perform related actions.
   */
  onKeypress() {
    this.render();
  }
}
