/**
 * `editor` type prompt
 */

import colors from 'yoctocolors-cjs';
import { editAsync } from 'external-editor';
import { Subject } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

export default class EditorPrompt extends Base {

  /**
   * Start the Inquiry session.
   *
   * @param {Function} cb - Callback function to be executed when the prompt is completed.
   * @return {this}
   */
  _run(cb) {
    this.done = cb;

    this.editorResult = new Subject();

    // Open Editor on "line" (Enter Key)
    const events = observe(this.rl);
    this.lineSubscription = events.line.subscribe(this.startExternalEditor.bind(this));
    const waitUserInput =
      this.opt.waitUserInput === undefined ? true : this.opt.waitUserInput;

    // Trigger Validation when editor closes
    const validation = this.handleSubmitEvents(this.editorResult);
    validation.success.forEach(this.onEnd.bind(this));
    validation.error.forEach(this.onError.bind(this));

    // Prevents default from being printed on screen (can look weird with multiple lines)
    this.currentText = this.opt.default;
    this.opt.default = null;

    // Init
    if (waitUserInput) {
      this.render();
    } else {
      this.startExternalEditor();
    }

    return this;
  }


  /**
   * Render the prompt to screen.
   *
   * @param {string} error - Optional error message to display at the bottom of the screen.
   * @return {EditorPrompt} self - Returns the current instance for method chaining.
   */
  render(error) {
    let bottomContent = '';
    let message = this.getQuestion();

    message +=
      this.status === 'answered'
        ? colors.dim('Received')
        : colors.dim('Press <enter> to launch your preferred editor.');

    if (error) {
      bottomContent = colors.red('>> ') + error;
    }

    this.screen.render(message, bottomContent);
  }


  /**
   * Launches an external editor to allow user input.
   *
   * This function pauses the Readline interface to prevent modifications to stdin and stdout while the editor is active. It then calls `editAsync` with the current text, a callback to handle post-editor actions, and an optional postfix for the file extension.
   *
   * @function
   * @name startExternalEditor
   * @returns {void} - This function does not return any value.
   */
  startExternalEditor() {
    // Pause Readline to prevent stdin and stdout from being modified while the editor is showing
    this.rl.pause();
    editAsync(this.currentText, this.endExternalEditor.bind(this), {
      postfix: this.opt.postfix ?? '.txt',
    });
  }

  /**
   * Resumes reading from the readline interface and handles the result of an external editor.
   *
   * @param {Error} error - The error object if an error occurred, otherwise null.
   * @param {*} result - The result returned by the external editor.
   */
  endExternalEditor(error, result) {
    this.rl.resume();
    if (error) {
      this.editorResult.error(error);
    } else {
      this.editorResult.next(result);
    }
  }

  /**
   * Handles the end of an interaction or processing state.
   *
   * @param {Object} state - The current state object containing relevant data.
   * @param {string} state.value - The value associated with the current state.
   * @return {void}
   */
  onEnd(state) {
    this.editorResult.unsubscribe();
    this.lineSubscription.unsubscribe();
    this.answer = state.value;
    this.status = 'answered';
    // Re-render prompt
    this.render();
    this.screen.done();
    this.done(this.answer);
  }

  /**
   * Handles error state by rendering the validity of the current state.
   *
   * @param {Object} state - The current state object containing validation information.
   */
  onError(state) {
    this.render(state.isValid);
  }
}
