/**
 * `input` type prompt
 */

import colors from 'yoctocolors-cjs';
import { map, takeUntil } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

export default class InputPrompt extends Base {

  /**
   * Start the Inquiry session.
   *
   * @param {Function} cb - Callback function to be executed when the prompt is completed.
   * @returns {this}
   */
  _run(cb) {
    this.done = cb;

    // Once user confirm (enter key)
    const events = observe(this.rl);
    const submit = events.line.pipe(map(this.filterInput.bind(this)));

    const validation = this.handleSubmitEvents(submit);
    validation.success.forEach(this.onEnd.bind(this));
    validation.error.forEach(this.onError.bind(this));

    events.keypress
      .pipe(takeUntil(validation.success))
      .forEach(this.onKeypress.bind(this));

    // Init
    this.render();

    return this;
  }


  /**
   * Renders the prompt to the screen.
   *
   * @param {string} error - An optional error message to display below the prompt. If provided, it will be displayed in red.
   * @return {InputPrompt} self - Returns the current instance of `InputPrompt` for method chaining.
   */
  render(error) {
    let bottomContent = '';
    let appendContent = '';
    let message = this.getQuestion();
    const { transformer } = this.opt;
    const isFinal = this.status === 'answered';

    appendContent = isFinal ? this.answer : this.rl.line;

    if (transformer) {
      message += transformer(appendContent, this.answers, { isFinal });
    } else {
      message += isFinal ? colors.cyan(appendContent) : appendContent;
    }

    if (error) {
      bottomContent = colors.red('>> ') + error;
    }

    this.screen.render(message, bottomContent);
  }


  /**
   * Filters or processes user input when the `enter` key is pressed.
   *
   * @param {string} input - The user input to be processed.
   * @returns {string} - The filtered or processed input.
   *
   * Example:
   * filterInput('Hello World'); // returns 'Hello World'
   * filterInput(null);          // returns ''
   */
  filterInput(input) {
    if (!input) {
      return this.opt.default == null ? '' : this.opt.default;
    }

    return input;
  }

  /**
   * Handles the end of a language-related process.
   *
   * @param {Object} state - The current state object containing relevant data.
   * @param {string} state.value - The value to be assigned to the answer property.
   * @returns {void}
   *
   * This method is called when the language processing ends. It updates the answer
   * with the provided state value, sets the status to 'answered', and then renders
   * the prompt again. After rendering, it marks the screen as done and calls the
   * done callback function with the state value.
   *
   * Example:
   * onEnd({ value: "Hello, world!" });
   */
  onEnd(state) {
    this.answer = state.value;
    this.status = 'answered';

    // Re-render prompt
    this.render();

    this.screen.done();
    this.done(state.value);
  }

  /**
   * Handles an error by appending its value to the current input line and updating the cursor position.
   *
   * @param {Object} options - The options object containing the error details.
   * @param {string} [options.value=''] - The error message or additional text to append.
   * @param {boolean} [options.isValid=false] - Indicates whether the current input is valid after handling the error.
   *
   * @returns {undefined}
   */
  onError({ value = '', isValid }) {
    this.rl.line += value;
    this.rl.cursor += value.length;
    this.render(isValid);
  }


  /**
   * This method is triggered when a user presses a key. It updates the status of the object to 'touched' and then calls the render method.
   *
   * @returns {void} - This method does not return any value.
   */
  onKeypress() {
    this.status = 'touched';

    this.render();
  }
}
