/**
 * `input` type prompt
 */

import Input from './input.js';

/**
 * Extention of the Input prompt specifically for use with number inputs.
 */

export default class NumberPrompt extends Input {
  /**
   * Filters the input to return a number if possible. If the input is not a valid number or is invalid,
   * it returns the default value.
   *
   * @param {string} input - The input string to be filtered.
   * @returns {number} - The filtered number if the input is a valid number, otherwise the default value.
   *
   * Examples:
   * - filterInput("123") should return 123
   * - filterInput("-123.45") should return -123.45
   * - filterInput("abc") should return NaN (default value)
   */
  filterInput(input) {
    if (input && typeof input === 'string') {
      input = input.trim();
      // Match a number in the input
      const numberMatch = input.match(/(^-?\d+|^-?\d+\.\d*|^\d*\.\d+)(e\d+)?$/);
      // If a number is found, return that input.
      if (numberMatch) {
        return Number(numberMatch[0]);
      }
    }

    // If the input was invalid return the default value.
    return this.opt.default == null ? Number.NaN : this.opt.default;
  }
}
