/**
 * `password` type prompt
 */

import colors from 'yoctocolors-cjs';
import { map, takeUntil } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

/**
 * Masks an input string by replacing all characters with a specified mask character.
 *
 * @param {string} input - The input string to be masked.
 * @param {string} [maskChar='*'] - The character used to mask the input. Defaults to '*'.
 * @returns {string} - The masked string.
 *
 * @example
 * // Returns '*****'
 * mask('hello');
 *
 * @example
 * // Returns '###'
 * mask('123', '#');
 */
function mask(input, maskChar) {
  input = String(input);
  maskChar = typeof maskChar === 'string' ? maskChar : '*';
  if (input.length === 0) {
    return '';
  }

  return Array.from({ length: input.length + 1 }).join(maskChar);
}

export default class PasswordPrompt extends Base {

  /**
   * Start the Inquiry session.
   *
   * @param {Function} cb - Callback function to execute when the prompt is done.
   * @returns {this} - The current instance of the object for method chaining.
   */
  _run(cb) {
    this.done = cb;

    const events = observe(this.rl);

    // Once user confirm (enter key)
    const submit = events.line.pipe(map(this.filterInput.bind(this)));

    const validation = this.handleSubmitEvents(submit);
    validation.success.forEach(this.onEnd.bind(this));
    validation.error.forEach(this.onError.bind(this));

    events.keypress
      .pipe(takeUntil(validation.success))
      .forEach(this.onKeypress.bind(this));

    // Init
    this.render();

    return this;
  }


  /**
   * Render the prompt to screen.
   *
   * @param {string} [error] - Optional error message to display below the prompt.
   * @return {PasswordPrompt} - Returns self for method chaining.
   */
  render(error) {
    let message = this.getQuestion();
    let bottomContent = '';

    message +=
      this.status === 'answered'
        ? this.getMaskedValue(this.answer)
        : this.getMaskedValue(this.rl.line || '');

    if (error) {
      bottomContent = '\n' + colors.red('>> ') + error;
    }

    this.screen.render(message, bottomContent);
  }

  /**
   * Returns a masked version of the input value based on the current status and options.
   *
   * @param {string} value - The input value to be masked.
   * @returns {string} - The masked value or a placeholder indicating hidden input.
   */
  getMaskedValue(value) {
    if (this.status === 'answered') {
      return this.opt.mask
        ? colors.cyan(mask(value, this.opt.mask))
        : colors.italic(colors.dim('[hidden]'));
    }
    return this.opt.mask
      ? mask(value, this.opt.mask)
      : colors.italic(colors.dim('[input is hidden] '));
  }

  /**
   * Gets the masked value during async filter/validation.
   *
   * @param {string} value - The input value to be masked.
   * @returns {string} - The masked value.
   */
  getSpinningValue(value) {
    return this.getMaskedValue(value);
  }


  /**
   * Filters input to handle cases where it is `null` or `undefined`.
   *
   * @param {string} input - The input string to be filtered.
   * @returns {string} - The filtered input, or the default value if the input is falsy.
   */
  filterInput(input) {
    if (!input) {
      return this.opt.default == null ? '' : this.opt.default;
    }

    return input;
  }

  /**
   * Called when the language session ends.
   *
   * This method updates the status to 'answered', sets the answer based on the state value,
   * re-renders the prompt, marks the screen as done, and then calls the done callback with the state value.
   *
   * @param {Object} state - The current state of the language session containing the answer value.
   */
  onEnd(state) {
    this.status = 'answered';
    this.answer = state.value;

    // Re-render prompt
    this.render();

    this.screen.done();
    this.done(state.value);
  }

  /**
   * Handles error state by rendering based on validation status.
   *
   * @param {Object} state - The current application state.
   * @param {boolean} state.isValid - Indicates whether the state is valid or not.
   * @throws {Error} - If the state object does not contain the expected properties.
   */
  onError(state) {
    this.render(state.isValid);
  }

  /**
   * Handles the keypress event by clearing the default value if set and re-rendering the component.
   *
   * This method is designed to be called whenever a key is pressed within the context of the class instance.
   * It checks if there is a default value set (`this.opt.default`). If so, it clears this value
   * using the logical assignment operator (`&&=`) to ensure `undefined` is assigned if the current value is falsy.
   *
   * After clearing the default value (if applicable), it calls the `render()` method to update the display
   * based on the current state of the component.
   */
  onKeypress() {
    // If user press a key, just clear the default value
    this.opt.default &&= undefined;

    this.render();
  }
}
