/**
 * Extracts the search query from a parsed URL.
 *
 * @param {URL} parsedURL - The parsed URL object.
 * @returns {string} - The search query string, or an empty string if no search is present.
 *
 * @example
 * const url = new URL('http://example.com?query=test');
 * console.log(getSearch(url)); // Output: '?query=test'
 */
export const getSearch = parsedURL => {
	if (parsedURL.search) {
		return parsedURL.search;
	}

	const lastOffset = parsedURL.href.length - 1;
	const hash = parsedURL.hash || (parsedURL.href[lastOffset] === '#' ? '#' : '');
	return parsedURL.href[lastOffset - hash.length] === '?' ? '?' : '';
};
