/**
 * Extracts the search query part from a parsed URL.
 *
 * @param {URL} parsedURL - The URL object to parse.
 * @returns {string} - The search query part of the URL, or an empty string if not found.
 *
 * @example
 * const url = new URL('https://example.com/search?q=javascript');
 * console.log(getSearch(url)); // '?q=javascript'
 */
export const getSearch = parsedURL => {
	if (parsedURL.search) {
		return parsedURL.search;
	}

	const lastOffset = parsedURL.href.length - 1;
	const hash = parsedURL.hash || (parsedURL.href[lastOffset] === '#' ? '#' : '');
	return parsedURL.href[lastOffset - hash.length] === '?' ? '?' : '';
};
