const redirectStatus = new Set([301, 302, 303, 307, 308]);

/**
 * Determines if the provided status code represents a redirect.
 *
 * @param {number} code - The HTTP status code to check.
 * @return {boolean} - Returns true if the status code is a redirect, false otherwise.
 */
export const isRedirect = code => {
	return redirectStatus.has(code);
};
