"use strict";

var BOMChar = '\uFEFF';

exports.PrependBOM = PrependBOMWrapper
/**
 * A wrapper function that prepends a Byte Order Mark (BOM) to the output of an encoder.
 *
 * @class
 * @param {Encoder} encoder - The encoder whose output will be wrapped with a BOM.
 * @param {Object} [options] - Optional configuration options.
 */
function PrependBOMWrapper(encoder, options) {
    this.encoder = encoder;
    this.addBOM = true;
}

PrependBOMWrapper.prototype.write = function(str) {
    if (this.addBOM) {
        str = BOMChar + str;
        this.addBOM = false;
    }

    return this.encoder.write(str);
}

PrependBOMWrapper.prototype.end = function() {
    return this.encoder.end();
}


//------------------------------------------------------------------------------

exports.StripBOM = StripBOMWrapper;
/**
 * Wraps a decoder to strip Byte Order Mark (BOM) from the input data.
 *
 * @constructor
 * @param {Object} decoder - The decoder object to wrap.
 * @param {Object} [options={}] - Optional configuration options for the wrapper.
 */
function StripBOMWrapper(decoder, options) {
    this.decoder = decoder;
    this.pass = false;
    this.options = options || {};
}

StripBOMWrapper.prototype.write = function(buf) {
    var res = this.decoder.write(buf);
    if (this.pass || !res)
        return res;

    if (res[0] === BOMChar) {
        res = res.slice(1);
        if (typeof this.options.stripBOM === 'function')
            this.options.stripBOM();
    }

    this.pass = true;
    return res;
}

StripBOMWrapper.prototype.end = function() {
    return this.decoder.end();
}

