import colors from 'yoctocolors-cjs';
import figures from '@inquirer/figures';

/**
 * Separator object
 * Used to space/separate choices group
 * @constructor
 * @param {String} line   Separation line content (facultative)
 */

export default class Separator {
  constructor(line) {
    this.type = 'separator';
    this.line = colors.dim(line || Array.from({ length: 15 }).join(figures.line));
  }

  /**
   * Helper function returning false if object is a separator.
   *
   * @param {Object} obj - The object to test against.
   * @returns {Boolean} - Returns `false` if the object is a separator.
   */
  static exclude(obj) {
    return obj.type !== 'separator';
  }

  /**
   * Converts the current object to its string representation.
   *
   * @returns {String} - The separator display string.
   */
  toString() {
    return this.line;
  }
}
