/**
 * `confirm` type prompt
 */

import colors from 'yoctocolors-cjs';
import { take, takeUntil } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

export default class ConfirmPrompt extends Base {
  constructor(questions, rl, answers) {
    super(questions, rl, answers);

    let rawDefault = true;

    Object.assign(this.opt, {
      filter(input) {
        if (input != null && input !== '') {
          if (/^y(es)?/i.test(input)) return true;
          if (/^n(o)?/i.test(input)) return false;
        }
        return rawDefault;
      },
    });

    if (this.opt.default != null) {
      rawDefault = Boolean(this.opt.default);
    }

    this.opt.default = rawDefault ? 'Y/n' : 'y/N';
  }


  /**
   * Start the Inquiry session.
   *
   * @param {Function} cb - Callback function to be executed when the prompt is done.
   * @return {this} - Returns the current instance of the class for method chaining.
   */
  _run(cb) {
    this.done = cb;

    // Once user confirm (enter key)
    const events = observe(this.rl);
    events.keypress.pipe(takeUntil(events.line)).forEach(this.onKeypress.bind(this));

    events.line.pipe(take(1)).forEach(this.onEnd.bind(this));

    // Init
    this.render();

    return this;
  }


  /**
   * Render the prompt to screen and display the user's answer.
   *
   * @param {boolean|string} answer - The user's response to the prompt. If a boolean, it will be converted to 'Yes' or 'No'. If a string, it will be appended as is.
   * @return {ConfirmPrompt} self - Returns the current instance of ConfirmPrompt for method chaining.
   *
   * Example usage:
   * const prompt = new ConfirmPrompt();
   * prompt.render(true); // Renders "Question? Yes"
   * prompt.render("Custom answer"); // Renders "Question? Custom answer"
   */
  render(answer) {
    let message = this.getQuestion();

    if (typeof answer === 'boolean') {
      message += colors.cyan(answer ? 'Yes' : 'No');
    } else if (answer) {
      message += answer;
    } else {
      message += this.rl.line;
    }

    this.screen.render(message);

    return this;
  }


  /**
   * Handles the end of user input when the 'enter' key is pressed.
   *
   * @param {string} input - The user input received.
   * @returns {void}
   */
  onEnd(input) {
    this.status = 'answered';

    let output = this.opt.filter(input);
    if (this.opt.transformer) {
      output = this.opt.transformer(output);
    }
    this.render(output);

    this.screen.done();
    this.done(output);
  }


  /**
   * Handles the keypress event and triggers rendering of the current state.
   */
  onKeypress() {
    this.render();
  }
}
