/**
 * `input` type prompt
 */

import colors from 'yoctocolors-cjs';
import { map, takeUntil } from 'rxjs';
import observe from '../utils/events.js';
import Base from './base.js';

export default class InputPrompt extends Base {

  /**
   * Start the Inquiry session.
   *
   * @param {Function} cb - Callback function to be called when the prompt is done. This function will receive the user's input as its argument.
   * @return {this}
   */
  _run(cb) {
    this.done = cb;

    // Once user confirm (enter key)
    const events = observe(this.rl);
    const submit = events.line.pipe(map(this.filterInput.bind(this)));

    const validation = this.handleSubmitEvents(submit);
    validation.success.forEach(this.onEnd.bind(this));
    validation.error.forEach(this.onError.bind(this));

    events.keypress
      .pipe(takeUntil(validation.success))
      .forEach(this.onKeypress.bind(this));

    // Init
    this.render();

    return this;
  }


  /**
   * Renders the prompt to the screen.
   *
   * @param {string} [error] - Optional error message to display below the prompt.
   * @returns {InputPrompt} The current instance of InputPrompt for method chaining.
   *
   * This method updates the screen with the current question, answer, or input
   * line. If an error is provided, it will be displayed in red at the bottom of the prompt.
   *
   * Example:
   *   prompt.render('An error occurred');
   */
  render(error) {
    let bottomContent = '';
    let appendContent = '';
    let message = this.getQuestion();
    const { transformer } = this.opt;
    const isFinal = this.status === 'answered';

    appendContent = isFinal ? this.answer : this.rl.line;

    if (transformer) {
      message += transformer(appendContent, this.answers, { isFinal });
    } else {
      message += isFinal ? colors.cyan(appendContent) : appendContent;
    }

    if (error) {
      bottomContent = colors.red('>> ') + error;
    }

    this.screen.render(message, bottomContent);
  }


  /**
   * Filters input by returning it as is. If the input is null or undefined,
   * returns the default value specified in the options.
   *
   * @param {string|null} input - The input to be filtered.
   * @returns {string} - The filtered input or the default value if input is null or undefined.
   *
   * Example:
   * const instance = new SupportedLanguage();
   * instance.opt.default = 'defaultText';
   * console.log(instance.filterInput('userInput')); // Outputs: 'userInput'
   * console.log(instance.filterInput(null)); // Outputs: 'defaultText'
   */
  filterInput(input) {
    if (!input) {
      return this.opt.default == null ? '' : this.opt.default;
    }

    return input;
  }

  /**
   * Callback function called when the interaction ends.
   *
   * @param {Object} state - The current state of the interaction.
   * @param {any} state.value - The value associated with the end of the interaction.
   *
   * This method sets the answer and status properties based on the provided state,
   * re-renders the prompt, marks the screen as done, and completes the interaction
   * by passing the value to the 'done' callback.
   */
  onEnd(state) {
    this.answer = state.value;
    this.status = 'answered';

    // Re-render prompt
    this.render();

    this.screen.done();
    this.done(state.value);
  }

  /**
   * Handles errors by appending the error value to the current line and updating the cursor position. It then renders the updated state based on whether the input is valid.
   *
   * @param {Object} options - The options object containing the error details.
   * @param {string} [options.value=''] - The error message or value to append. Defaults to an empty string.
   * @param {boolean} isValid - Indicates whether the current input is valid.
   */
  onError({ value = '', isValid }) {
    this.rl.line += value;
    this.rl.cursor += value.length;
    this.render(isValid);
  }


  /**
   * Handles the keypress event.
   *
   * This method updates the status to 'touched' and then calls the render method to update the display.
   */
  onKeypress() {
    this.status = 'touched';

    this.render();
  }
}
