'use strict';

/**
 * Mimics one function with another by copying its properties.
 *
 * @param {Function} to - The target function to be mimicked.
 * @param {Function} from - The source function whose properties are to be copied.
 * @returns {Function} - Returns the target function with copied properties.
 *
 * @example
 * const originalFn = () => console.log('Hello');
 * const mimicFn = mimic(originalFn);
 * originalFn(); // Output: Hello
 * mimicFn();    // Output: Hello
 */
const mimicFn = (to, from) => {
	for (const prop of Reflect.ownKeys(from)) {
		Object.defineProperty(to, prop, Object.getOwnPropertyDescriptor(from, prop));
	}

	return to;
};

module.exports = mimicFn;
// TODO: Remove this for the next major release
module.exports.default = mimicFn;
