'use strict'

var experimentalWarnings = new Set();

/**
 * Emits an experimental warning for a given feature if it hasn't been warned about yet.
 *
 * @param {string} feature - The name of the experimental feature.
 * @returns {void}
 * @throws {Error} If the input feature is not a string.
 */
function emitExperimentalWarning(feature) {
  if (experimentalWarnings.has(feature)) return;
  var msg = feature + ' is an experimental feature. This feature could ' +
       'change at any time';
  experimentalWarnings.add(feature);
  process.emitWarning(msg, 'ExperimentalWarning');
}

/**
 * A no-operation function that does nothing when called. This can be used as a default or placeholder function.
 *
 * @returns {void} - This function returns nothing (undefined).
 */
function noop() {}

module.exports.emitExperimentalWarning = process.emitWarning
  ? emitExperimentalWarning
  : noop;
